import React from 'react';
import { useForm } from 'react-hook-form';
import { createEmployee } from '../services/api';
import { TextField, Button } from '@mui/material';

const EmployeeForm = () => {
    const { register, handleSubmit, reset } = useForm();

    const onSubmit = async (data) => {
        await createEmployee(data);
        reset();
    };

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <TextField label="Name" {...register('name')} required />
            <TextField label="Designation" {...register('designation')} required />
            <TextField label="CTC" type="number" {...register('ctc')} required />
            <TextField label="Email" type="email" {...register('email')} required />
            <Button type="submit" variant="contained">Create Employee</Button>
        </form>
    );
};

export default EmployeeForm;
